sys_script_client.script:
function onSubmit() {
	var cdRef = g_form.getValue('u_cd_reference');
    var c = 'C';
    var d = 'D';
    var a = '@';

    if (cdRef.charAt(0) == c && cdRef.charAt(1) == d && cdRef.charAt(cdRef.length - 1) == a) {
        g_form.hideFieldMsg('u_cd_reference');
        alert('Saved'); // Just to prove that the save is happening 
    }
    else { 
        g_form.hideFieldMsg('u_cd_reference');    
        g_form.showFieldMsg('u_cd_reference','The value must start with "CD", and end with the character "@"' + cdRef, 'error',false);
        return false;
    }
}
